import React from "react";
import TagCloud from "react-tag-cloud"

const MyCloud = (props) => {
    //console.log(props.text)

    const cloud = props.text.map((droplet) => {
        console.log(droplet.word)
        return <div key={Math.floor(Math.random() * 10)}> {droplet.word}</div>
    })

    return (
        <TagCloud
            className='tag-cloud'
            style={{
                fontFamily: 'sans-serif',
                fontSize: 30,
                fontWeight: 'bold',
                fontStyle: 'italic',
                color: "blue",
                padding: 5,
                width: '100%',
                height: '100%'
            }}>
            <div> {props.text.length === 0 ? null : cloud} </div>

        </TagCloud>
    )

}

export default MyCloud;
