import React, { Component } from "react";
//import Goo from 'gooey-react'
import Typography from '@material-ui/core/Typography';
import randomColor from "randomcolor"
import "./wordcloud.css"
import gif from "./../../media/tweetgifv7.gif"
import Grid from '@material-ui/core/Grid';

//import test from "./../../media/test.png"

const styles = {
    large: {
        fontSize: 60,
        fontWeight: 'bold'
    },
    small: {
        opacity: 0.7,
        fontSize: 16
    }
};



const MyCloud = (props) => {



    const droplets = props.text.map((droplet) => (
        <div key={props.text.indexOf(droplet)} className="droplet"
            style={{ fontSize: `${droplet.frequency * .6}em`, fontWeight: "bold", padding: "2px" }}
        >  {droplet.word} </div>
    ))

    console.log(props.text)

    return (
        <Grid container direction="column">
            <div className="cloudWrapper">

                <div className="cloudTitle">
                    <Typography variant="h2"> Word Cloud from @{props.accountName}'s feed </Typography>

                </div>
                <div className='app-outer'>

                    <div style={{

                    }}>

                        <img src={gif} alt="tweetGif" style={{ borderRadius: "16px", height: "950px" }} />

                    </div>

                    <div className="cloud">
                        {droplets}
                    </div>



                </div>
            </div >
        </Grid>
    );

}


export default MyCloud;

