import React, { useState } from "react";
import "./minimize.css"

const CollapseButton = (props) => {

    // document.querySelector('.circle').addEventListener('click', () => {
    //     document.querySelector('.vertical').classList.toggle('open');
    //     document.querySelector('.line-wrapper').classList.toggle('open');
    //     document.querySelector('.content').classList.toggle('open');
    //   });

    // toggle class hook from customName to customName.open

    const [btnStatus, setBtnStatus] = useState(true) // defaults to plus 

    const changeBtnStatus = () => {
        setBtnStatus(!btnStatus)
    }

    // const btnClassNamesOn = {   //When tweets are available 
    //     lineClass: "vertical",
    //     wrapperClass: "line-wrapper"
    // }

    // const btnClassNamesOff = { //When tweets are minimized
