import React from "react";
import "./currentlyviewing.css"

import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
//import Grid from '@material-ui/core/Grid';

const CurrentlyViewing = (props) => {
    const useStyles = makeStyles(theme => ({ //button styling
        root: {
            color: "#28244E",
            borderColor: "#28244E",
            borderWidth: "5px",
            borderRadius: "16px",




            '&:hover': {
                color: "#3ACBA3 ",
                borderColor: "white",
                borderWidth: "5px",
            },

        }
    }));
    const classes = useStyles();

    const handleClick = () => {
        props.changeStatus(!props.status)
    }

    return (
        <div className="current_view_wrapper">

            <p>  See @{props.handle}'s feed below</p>

            <Button variant="outlined" color="primary" size="large" className={classes.root}
                onClick={handleClick} style={{ paddingBottom: "50px" }}
            >
                <span style={{ fontSize: "20px" }}>&#8617;</span>

            </Button>


        </div >
    )
}

export default CurrentlyViewing;
