    return (
        <div className='app-outer'>

            <div className='app-inner'>

                <h1> Word Cloud </h1>

                <TagCloud
                    className='tag-cloud'
                    style={{
                        fontFamily: 'sans-serif',
                        //fontSize: () => Math.round(Math.random() * 50) + 16,
                        fontSize: 30,
                        color: () => randomColor({
