import React from "react";
import "./currentlyviewing.css"

import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
//import Grid from '@material-ui/core/Grid';

const CurrentlyViewing = (props) => {
    const useStyles = makeStyles(theme => ({ //button styling
        root: {
            color: "#28244E",
            borderColor: "#64636B",
            borderWidth: "5px",
            borderRadius: "16px",
            background: "-webkit-linear-gradient(45deg, #2F3639, #D2DEE3, #89EBCF 80%)",
            opacity: ".8",




            '&:hover': {
                color: "#3ACBA3 ",
                borderColor: "white",
                borderWidth: "5px",
            },

        }
    }));
    const classes = useStyles();

    const handleClick = () => {
        props.changeStatus(!props.status)
    }

    return (
        <div className="current_view_wrapper">

            <p>  See @{props.handle}'s feed below</p>

            <Button variant="outlined" color="primary" size="large" className={classes.root}
                onClick={handleClick} style={{ marginBottom: "5px" }}
            >
                <i class="fas fa-undo" style={{ fontSize: "20px", color: "#64636B" }}></i>

            </Button>


        </div >
    )
}

export default CurrentlyViewing;

