import React from "react";
import TagCloud from "react-tag-cloud"

const MyCloud = (props) => {
    console.log(props.text)

    const cloud = props.text.map((droplet) => {
        <div> droplet.word</div>
    })

    return (
        <TagCloud
            className='tag-cloud'
            style={{
                fontFamily: 'sans-serif',
                //fontSize: () => Math.round(Math.random() * 50) + 16,
                fontSize: 30,
                color: "blue",
                // color: () => randomColor({
                //     hue: 'blue'
                // }),
                padding: 5,
            }}>
            <div> {cloud} </div>

        </TagCloud>
    )

}

export default MyCloud;
