import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

const AuthPages = (props) => {
  const [signupForm, setSignupForm] = useState("");
  const history = useHistory();
  console.log(props.history.location.state.loginOrSignup);
  useEffect(() => {
    const userHasRefreshed = props.history.state; //this is true when page is selected
    console.log(userHasRefreshed);
    if (!userHasRefreshed) {
      const userDecision = props.history.location.state.loginOrSignup;
      console.log(userDecision);
      setSignupForm(userDecision);
    } //else {
    // history.push("/");
    //}
  }, []);

  return (
    <div className="auth-page-wrapper">
      {/* logo top corner*/}
      <section className="auth-form-box">
        {signupForm === "Signup" ? (
          <div>
            <h1>Sign up</h1>
            <form>
              <label>Username</label>
              <input type="text" className="auth-input" />
              <label>Password</label>
              <input type="text" className="auth-input" />
              <label>Confirm Password</label>
              <input type="text" className="auth-input" />
              <button type="submit" className="auth-btn">
                {" "}
                Sign Up
              </button>
            </form>
          </div>
        ) : (
          <div>
            <h1>Login</h1>
            <form>
              <label>Username</label>
              <input type="text" className="auth-input" />
              <label>Password</label>
              <input type="text" className="auth-input" />
              <button type="submit" className="auth-btn">
                {" "}
                Login
              </button>
            </form>
          </div>
        )}
      </section>
    </div>
  );
};

export default AuthPages;
