          <Instructions />
        </Grid>

        <Grid item>
          <Input handleInput={handleInput} />
        </Grid>

        <Grid item>
          <section>
            <TwitterResults />
          </section>
        </Grid>

      </Grid>
    </div>
  );
}

export default App;
